  `url` varchar(255) NOT NULL DEFAULT '' COMMENT '网站网址',
  `site_logo` varchar(255) NOT NULL DEFAULT '',
  `site_address` varchar(255) NOT NULL DEFAULT '' COMMENT '公司地址',
  `site_email` varchar(100) NOT NULL DEFAULT '' COMMENT '邮箱',
  `site_filing` varchar(100) NOT NULL DEFAULT '' COMMENT '备案号',
  `site_jstransfer` text COMMENT 'js代码调用',
  `site_copyright` text COMMENT '底部版权',
  `site_fax` varchar(100) NOT NULL DEFAULT '',
  `file_size` int(11) unsigned NOT NULL DEFAULT '0' COMMENT '文件上传限制',
  PRIMARY KEY (`siteid`)
) ENGINE=MyISAM AUTO_INCREMENT=2 DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC;

-- ----------------------------
-- Records of xg_site
-- ----------------------------

-- ----------------------------
-- Table structure for xg_social
-- ----------------------------
DROP TABLE IF EXISTS `xg_social`;
CREATE TABLE `xg_social` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT 'ID',
  `title` varchar(100) NOT NULL DEFAULT '',
  `sort` smallint(6) unsigned NOT NULL DEFAULT '0',
  `status` tinyint(1) unsigned NOT NULL DEFAULT '1' COMMENT '0-锁定，1-正常',
  `inputtime` int(10) unsigned NOT NULL DEFAULT '0',
  `types` tinyint(1) unsigned NOT NULL DEFAULT '1' COMMENT '1-工作地点，2-职业类型',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=10 DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC;

