INSERT INTO `xg_article` VALUES ('111', '2014年', '', '1405504757', '1', '', '', '', '/Uploads/Images/2018-08-09/5b6c10c8c105e.JPG', '49', '1405504757', '本站', '0', 'admin', '', '0', '0', '正虹油烟在线监测系统升级到V5.0<br/>\n广州市天河区正虹油烟在线监测系统建成投入运营；<br/>\n受中央电视台《影响力对话》栏目邀请，公司总经理谷总与主持人阿丘中国环境现状的《影响力对话》；<br/>\n成功研发VOCs治理技术：多相催化氧化、Uv光量子光解、等离子催化等技术；', '', '0', '');
