INSERT INTO `xg_product` VALUES ('20', '正虹手持式TVOC气体检测仪', '<p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-family: 宋体, SimSun; font-size: 14px;\"><strong><span style=\"font-family: 宋体, SimSun; color: rgb(51, 127, 229);\">产品概述</span></strong></span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-family: 宋体, SimSun; font-size: 14px;\">&nbsp;</span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-family: 宋体, SimSun; font-size: 14px;\">正虹手持式TVOC气体检测仪是一种可监测各种有机挥发性气体的手持式检测设备。主要用于总量VOC气体在线/离线监控，是一款充分利用传感技术、物联网、无线通讯和GPS定位技术的手持式气体检测设备。方便环保局工作人员巡检和企业自行检测以及第三方检测鉴定。</span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-family: 宋体, SimSun; font-size: 14px;\">&nbsp;</span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-family: 宋体, SimSun; font-size: 14px;\">本检测仪可用于TVOC气体等污染源的模拟/数字监控数据进行采集，并根据要求上传至指定服务器。支持国标212协议。</span></p><p style=\"word-wrap: break-word;margin-left: 0;font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica;font-size: 12px;line-height: 18px;white-space: normal;widows: auto;text-indent: 28px;text-align: justify\"><br/></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-align: center; text-indent: 28px; line-height: 1.5em;\"><img alt=\"\" src=\"/Upload/image/20180606/1528269204124581.jpg\"/><br/></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-family: 宋体, SimSun; font-size: 14px;\">&nbsp;</span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-family: 宋体, SimSun; font-size: 14px;\"><strong><span style=\"font-size: 14px; font-family: 宋体, SimSun; color: rgb(51, 127, 229);\">产品特点</span></strong></span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-family: 宋体, SimSun; font-size: 14px;\">&nbsp;</span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-family: 宋体, SimSun; font-size: 14px;\">全彩7寸液晶触摸屏，彩色液晶显示直观，操作性好；</span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-family: 宋体, SimSun; font-size: 14px;\">&nbsp;</span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-family: 宋体, SimSun; font-size: 14px;\">具有温、湿度、总VOC浓度测量功能；</span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-family: 宋体, SimSun; font-size: 14px;\">&nbsp;</span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-family: 宋体, SimSun; font-size: 14px;\">气体浓度以数字和曲线两种方式显示；</span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-family: 宋体, SimSun; font-size: 14px;\">&nbsp;</span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-family: 宋体, SimSun; font-size: 14px;\">具有音频报警功能；</span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-family: 宋体, SimSun; font-size: 14px;\">&nbsp;</span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-family: 宋体, SimSun; font-size: 14px;\">可配置CF校准系数；</span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-family: 宋体, SimSun; font-size: 14px;\">&nbsp;</span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-family: 宋体, SimSun; font-size: 14px;\">具有测试记录功能，可存储1个月的测量数据；</span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-family: 宋体, SimSun; font-size: 14px;\">&nbsp;</span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-family: 宋体, SimSun; font-size: 14px;\">具有标定记录功能；可记录最近4次标定时间与量程大小；</span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-family: 宋体, SimSun; font-size: 14px;\">&nbsp;</span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-family: 宋体, SimSun; font-size: 14px;\">采用智能探头设计，更方便日后标定与维护；</span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-family: 宋体, SimSun; font-size: 14px;\">&nbsp;</span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-family: 宋体, SimSun; font-size: 14px;\">采用高容量锂电池供电，可充电，低电量自动关机；</span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-family: 宋体, SimSun; font-size: 14px;\">&nbsp;</span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-family: 宋体, SimSun; font-size: 14px;\">GPRS无线数据上传通信功能及GPS定位功能；</span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-family: 宋体, SimSun; font-size: 14px;\">&nbsp;</span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-family: 宋体, SimSun; font-size: 14px;\">气体污染源数据上传协议支持HJ/T212协议；</span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-family: 宋体, SimSun; font-size: 14px;\">&nbsp;</span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-family: 宋体, SimSun; font-size: 14px;\"><strong><span style=\"font-size: 14px; font-family: 宋体, SimSun; color: rgb(51, 127, 229);\">设计标准</span></strong></span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-family: 宋体, SimSun; font-size: 14px;\">&nbsp;</span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-family: 宋体, SimSun; font-size: 14px;\">《H477-2009污染源在线自动监控（监测）数据采集传输仪技术要求》</span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-family: 宋体, SimSun; font-size: 14px;\">&nbsp;</span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-family: 宋体, SimSun; font-size: 14px;\">《SJ946-1983电子测量仪器电气、机械结构基本要求》</span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-family: 宋体, SimSun; font-size: 14px;\">&nbsp;</span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-family: 宋体, SimSun; font-size: 14px;\">《HJT212-2005污染源在线自动监控（监测）系统数据传输标准》</span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-family: 宋体, SimSun; font-size: 14px;\">&nbsp;</span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-family: 宋体, SimSun; font-size: 14px;\">《HJT212-2005污染源在线自动监控（监测）系统数据传输标准》</span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-family: 宋体, SimSun; font-size: 14px;\">&nbsp;</span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-family: 宋体, SimSun; font-size: 14px;\">《JJF1172-2007挥发性有机化合物光离子化检测仪校准规范》</span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-family: 宋体, SimSun; font-size: 14px;\">&nbsp;</span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-family: 宋体, SimSun; font-size: 14px;\"><strong><span style=\"font-size: 14px; font-family: 宋体, SimSun; color: rgb(51, 127, 229);\">适用行业</span></strong></span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-family: 宋体, SimSun; font-size: 14px;\">&nbsp;</span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-family: 宋体, SimSun; font-size: 14px;\">应用领域包括石油化工行业、电力行业，水处理行业、烟草行业、制药行业、食品行业、煤炭、冶金、市政燃气、环境卫生监测等多种场合现场检测。</span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-family: 宋体, SimSun; font-size: 14px;\">&nbsp;</span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-family: 宋体, SimSun; font-size: 14px;\"><strong><span style=\"font-size: 14px; font-family: 宋体, SimSun; color: rgb(51, 127, 229);\">产品功能</span></strong></span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-family: 宋体, SimSun; font-size: 14px;\">&nbsp;</span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-family: 宋体, SimSun; font-size: 14px;\"><strong>TVOC</strong><strong>气体浓度检测</strong>：测量精度：±10%以内</span></p><p style=\"word-wrap: break-word;margin-left: 0;font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica;font-size: 12px;line-height: 18px;white-space: normal;widows: auto;text-indent: 28px;text-align: justify\"><br/></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-family: 宋体, SimSun; font-size: 14px;\">采用前沿传感器技术，内置PID传感器能快速准确检测出被测气体的浓度。PID传感器是一种非破坏性传感器，它不会“燃烧”或永久性破坏待测气体。设备采用温湿度校准算法，让气体浓度检测更精准。采用泵吸式检测方法，便于取样检测。设备内置先进的温湿度传感器，采样数据自动转化为标压下数值。<br/></span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-family: 宋体, SimSun; font-size: 14px;\">&nbsp;</span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-family: 宋体, SimSun; font-size: 14px;\"><strong>测量曲线显示</strong>：直观显示当前气体的温度、湿度和浓度值</span></p><p style=\"word-wrap: break-word;margin-left: 0;font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica;font-size: 12px;line-height: 18px;white-space: normal;widows: auto;text-indent: 28px;text-align: justify\"><br/></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-family: 宋体, SimSun; font-size: 14px;\">在7英寸的大屏幕上用曲线直观显示当前气体的温度、湿度和浓度值，查看数据一目了然。<br/></span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-family: 宋体, SimSun; font-size: 14px;\">&nbsp;</span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-family: 宋体, SimSun; font-size: 14px;\"><strong>无线定位</strong><strong>/</strong><strong>无线传输</strong>：位置误差：＜2.5m CEP&nbsp;即时数据发送、批量数据发送、状态信息发送</span></p><p style=\"word-wrap: break-word;margin-left: 0;font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica;font-size: 12px;line-height: 18px;white-space: normal;widows: auto;text-indent: 28px;text-align: justify\"><br/></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-family: 宋体, SimSun; font-size: 14px;\">在检测现场就能及时把检测结果传输到监控中心（环保局等环境监控单位）。具有即时数据发送、批量数据发送、状态信息发送功能。<br/></span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-family: 宋体, SimSun; font-size: 14px;\">&nbsp;</span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-family: 宋体, SimSun; font-size: 14px;\"><strong>超标报警</strong>：当气体浓度超过设定值时，超标报警</span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-family: 宋体, SimSun; font-size: 14px;\">&nbsp;</span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-family: 宋体, SimSun; font-size: 14px;\">当气体浓度超过设定值时，超标报警，会弹出报警提示窗口。如果设置了声音报警，则蜂鸣器会短响三声。</span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-family: 宋体, SimSun; font-size: 14px;\">&nbsp;</span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-family: 宋体, SimSun; font-size: 14px;\"><strong><span style=\"font-size: 14px; font-family: 宋体, SimSun; color: rgb(51, 127, 229);\">产品特性</span></strong></span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-family: 宋体, SimSun; font-size: 14px;\">&nbsp;</span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-family: 宋体, SimSun; font-size: 14px;\"><strong>数据存储</strong>：设备最大支持16G SD存储卡，能及时存储实时数据并保存历史数据。存储单元具备断电保护功能，断电后所存储数据不丢失。</span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-family: 宋体, SimSun; font-size: 14px;\">&nbsp;</span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-family: 宋体, SimSun; font-size: 14px;\"><strong>异常续传</strong>：具有通讯故障修复后的续传功能。该功能保证了在无线通信网络信号较差，通信不稳定，设备仍然监控排放情况。</span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-family: 宋体, SimSun; font-size: 14px;\">&nbsp;</span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-family: 宋体, SimSun; font-size: 14px;\"><strong>精准定位：</strong>内置GPS定位模块，测量数据具有GPS位置信息，便于快速锁定污染源的位置。并且可以通过GPS位置信息确认数据的有效性。位置误差：＜2.5m CEP</span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-family: 宋体, SimSun; font-size: 14px;\">&nbsp;</span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-family: 宋体, SimSun; font-size: 14px;\"><strong>超长续航</strong>：内置7.4V 5200mAh高容量免维护锂电池，可连续工作8小时</span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-family: 宋体, SimSun; font-size: 14px;\">&nbsp;</span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-family: 宋体, SimSun; font-size: 14px;\"><strong><span style=\"font-size: 14px; font-family: 宋体, SimSun; color: rgb(51, 127, 229);\">通讯模块</span></strong></span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-family: 宋体, SimSun; font-size: 14px;\">&nbsp;</span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-family: 宋体, SimSun; font-size: 14px;\">设备采用知名厂商工业级通讯模块，稳定可靠，支持联通或移动卡；采用TCP通信方式，实时在线，及时在现场主动上报数据。</span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-family: 宋体, SimSun; font-size: 14px;\">&nbsp;</span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-family: 宋体, SimSun; font-size: 14px;\"><strong>联网性能</strong>：时间误差：＜1s，在GPS信号正常情况下，与卫星进行时间同步；</span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-family: 宋体, SimSun; font-size: 14px;\">&nbsp;</span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-family: 宋体, SimSun; font-size: 14px;\"><strong>通讯协议</strong>：默认支持HJ/T212-2005，支持自定义协议定制开发。</span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-family: 宋体, SimSun; font-size: 14px;\">&nbsp;</span></p><p><br/></p>', '1528269238', '1', '', '', '', '/Uploads/Images/2018-06-06/5b17898be4637.png', '15', '1528269238', '0', '', null, '74', '', '', '', '', '0', '9999', '0', '', '', '', '', '正虹手持式TVOC气体检测仪可用于TVOC气体等污染源的模拟/数字监控数据进行采集，并根据要求上传至指定服务器。', '0', '0', '');
INSERT INTO `xg_product` VALUES ('21', '区域挥发性有机物VOCs在线监测系统', '<p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-size: 14px; font-family: 宋体, SimSun;\">区域挥发性有机物VOCs是相对烟囱、烟道等有组织排放而言的露天环境中的无组织排放设施或无组织排放的车间、工棚等地的挥发性有机物VOCs。进行无组织排放污染源监测时，通常在监控点采集空气样品，捕捉污染物的最高浓度。</span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-size: 14px; font-family: 宋体, SimSun;\">&nbsp;</span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-size: 14px; font-family: 宋体, SimSun;\"><strong>系统概述：</strong></span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-size: 14px; font-family: 宋体, SimSun;\">&nbsp;</span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-size: 14px; font-family: 宋体, SimSun;\">区域挥发性有机物VOCs在线监测系统区别于有组织VOCs在线监控系统，增加了对风速、风向、温度、湿度等气象数据的检测和分析。该系统结合GIS系统，对污染物的扩散趋势进行推算，同时可对园区、厂界区域及泄漏点区域排放的VOCs浓度进行监测以及对监测区域气象参数进行监测及分析，为控制无组织排放挥发性有机物VOCS，减少大气污染、节省企业成本提供有效的技术支持。</span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-size: 14px; font-family: 宋体, SimSun;\">&nbsp;</span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-size: 14px; font-family: 宋体, SimSun;\"><strong>功能特点：</strong></span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-size: 14px; font-family: 宋体, SimSun;\">&nbsp;</span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-size: 14px; font-family: 宋体, SimSun;\">△可对气象数据进行监测及分析，包括风速、风向、温度、湿度等；</span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-size: 14px; font-family: 宋体, SimSun;\">&nbsp;</span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-size: 14px; font-family: 宋体, SimSun;\">△可自动分辨、测量多种不同气体，抗干扰能力强</span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-size: 14px; font-family: 宋体, SimSun;\">&nbsp;</span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-size: 14px; font-family: 宋体, SimSun;\">△可推算污染物扩散趋势</span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-size: 14px; font-family: 宋体, SimSun;\">&nbsp;</span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-size: 14px; font-family: 宋体, SimSun;\">△具备超标报警功能，对预设报警浓度做出及时、准确、直观的报警提示</span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-size: 14px; font-family: 宋体, SimSun;\">&nbsp;</span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-size: 14px; font-family: 宋体, SimSun;\"><span style=\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(51, 127, 229);\"><strong>区域挥发性有机物</strong></span><strong><span style=\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(51, 127, 229);\">VOCs</span></strong><span style=\"font-family: 宋体, SimSun; font-size: 14px; color: rgb(51, 127, 229);\"><strong>在线监测系统管理平台</strong></span></span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-size: 14px; font-family: 宋体, SimSun;\">&nbsp;</span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-size: 14px; font-family: 宋体, SimSun;\"><strong>平台简介：</strong></span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-size: 14px; font-family: 宋体, SimSun;\">&nbsp;</span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-size: 14px; font-family: 宋体, SimSun;\">区域挥发性有机物VOCs在线监测系统管理平台主要是对系统采集数据进行统一管理，方便用户进行查询、报表、统计等。平台为满足管理需要，提供合理的权限管理，不同权限的用户可完成制定的管理工作。支持无线及有线通讯方式，包括GPRS、CDMA、3G、Ethernet等。</span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-size: 14px; font-family: 宋体, SimSun;\">&nbsp;</span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-size: 14px; font-family: 宋体, SimSun;\"><strong>功能特点：</strong></span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-size: 14px; font-family: 宋体, SimSun;\">&nbsp;</span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-size: 14px; font-family: 宋体, SimSun;\">◇用户管理：可设置用户权限，不同权限的用户可完成制定的管理工作，保证系统的安全；</span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-size: 14px; font-family: 宋体, SimSun;\">&nbsp;</span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-size: 14px; font-family: 宋体, SimSun;\">◇报警功能：不仅提供报警数据显示，还提供了了用户确认报警数据的功能；</span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-size: 14px; font-family: 宋体, SimSun;\">&nbsp;</span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-size: 14px; font-family: 宋体, SimSun;\">◇数据查询：显示实时数据，并可进行临时数据报表查询功能；</span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-size: 14px; font-family: 宋体, SimSun;\">&nbsp;</span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-size: 14px; font-family: 宋体, SimSun;\">◇智能浏览：用户不仅可以按照区域、行业或受控级别来浏览各个监测点，还可以通过模糊查询，方便、快速地定位自己想要查询的监测点。</span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-size: 14px; font-family: 宋体, SimSun;\">&nbsp;</span></p><p><br/></p>', '1528271962', '1', '', '', '', '/Uploads/Images/2018-06-06/5b17944f51528.png', '16', '1528271962', '0', '', null, '38', '', '', '', '', '0', '9999', '0', '', '', '', '', '区域挥发性有机物VOCs在线监测系统区别于有组织VOCs在线监控系统，增加了对风速、风向、温度、湿度等气象数据的检测和分析。', '0', '0', '');
INSERT INTO `xg_product` VALUES ('22', '固定源挥发性有机物VOCs在线监控系统管理平台  ', '<p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-size: 14px; font-family: 宋体, SimSun;\"><strong>平台简介：</strong></span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-size: 14px; font-family: 宋体, SimSun;\"><strong>&nbsp;</strong></span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-size: 14px; font-family: 宋体, SimSun;\">环保主管部门或者企业用户可通过固定源挥发性有机物VOCs在线监控系统管理平台对VOCs监控数据进行管理、查询、统计、报表、打印、查看等操作，对VOCs进行实时监控。还可对历史数据、报警数据、治理设施去除率数据等进行查看管理和统计等，方便环保主管部门或企业用户统一管理、实时监控，及时发现异常。</span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-size: 14px; font-family: 宋体, SimSun;\">&nbsp;</span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-size: 14px; font-family: 宋体, SimSun;\"><strong>功能特点：</strong></span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-size: 14px; font-family: 宋体, SimSun;\">&nbsp;</span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-size: 14px; font-family: 宋体, SimSun;\">□&nbsp;24小时实时监控：实时采集数据，并以表格和动态曲线的方式显示在软件界面上；</span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-size: 14px; font-family: 宋体, SimSun;\">&nbsp;</span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-size: 14px; font-family: 宋体, SimSun;\">□ 支持多终端设备：可在电脑、手机、ipad等终端设备上查看数据信息；</span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-size: 14px; font-family: 宋体, SimSun;\">&nbsp;</span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-size: 14px; font-family: 宋体, SimSun;\">□ 历史数据查询：可以根据时间段自由选择查询历史实时数据、分钟数据、小时数据和日数据，并可以导出excel表格；</span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-size: 14px; font-family: 宋体, SimSun;\">&nbsp;</span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-size: 14px; font-family: 宋体, SimSun;\">□ 报警管理：实时超标报警，还可以自由设置报警声音和报警次数；</span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-size: 14px; font-family: 宋体, SimSun;\">&nbsp;</span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-size: 14px; font-family: 宋体, SimSun;\">□ 用户管理：可设置多级用户登录，方便管理，同时保证系统的安全；</span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-size: 14px; font-family: 宋体, SimSun;\">&nbsp;</span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-size: 14px; font-family: 宋体, SimSun;\">□ 污染源信息管理：可对企业基本信息、排口基本信息、生产工艺信息以及污染物信息进行录入管理；</span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-size: 14px; font-family: 宋体, SimSun;\">&nbsp;</span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-size: 14px; font-family: 宋体, SimSun;\">□ 数据报表分析：可以根据时间来制定日报、月报、季报和年报表。</span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-size: 14px; font-family: 宋体, SimSun;\">&nbsp;</span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-size: 14px; font-family: 宋体, SimSun;\">&nbsp;</span></p><p><br/></p>', '1528271936', '1', '', '', '', '/Uploads/Images/2018-06-06/5b17943d2cfc2.png', '16', '1528271936', '0', '', null, '59', '', '', '', '', '0', '9999', '0', '', '', '', '', '环保主管部门或者企业用户可通过固定源挥发性有机物VOCs在线监控系统管理平台对VOCs监控数据进行管理、查询、统计、报表、打印、查看等操作，对VOCs进行实时监控。', '0', '0', '');
INSERT INTO `xg_product` VALUES ('23', 'ZH-VOCs5100T挥发性有机物VOCs在线监测系统  ', '<p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-family: 宋体, SimSun; font-size: 14px;\"><strong>系统概述：</strong></span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-family: 宋体, SimSun; font-size: 14px;\">&nbsp;</span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-family: 宋体, SimSun; font-size: 14px;\">ZH-VOCs5100T挥发性有机物VOCs在线监测系统是一款基于气相色谱技术的在线监测系统，该系统采用光离子化检测器PID，可对30多种挥发性有机物（VOCs），如苯，甲苯，二甲苯有灵敏响应，精准检测。采用独特的智能零点跟踪技术获得非常准确的零点，用于检测到ppb的VOC气体。此外该系统还可根据需求扩展至其它多种挥发性组分（例如酮类、脂类等有机物）的监测，适用于石油化工、橡胶制品、半导体制造、医药、印刷、喷涂、有机溶剂制造、汽车制造等工业固定污染源的有机物排放监测。</span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-family: 宋体, SimSun; font-size: 14px;\">&nbsp;</span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-space: normal; widows: auto; text-indent: 28px; text-align: justify; line-height: 1.5em;\"><span style=\"font-family: 宋体, SimSun; font-size: 14px;\"><strong>系统功能特点：</strong></span></p><p style=\"word-wrap: break-word; margin-left: 0px; font-family: &#39;sans serif&#39;, tahoma, verdana, helvetica; font-size: 12px; white-spac